
public class Odd_Even_while {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int i=0,n=20;
        while(i<n)
        {
        	if(i%2==0)
        	{
        		System.out.println(i+"is even");
        	}
        	else
        	{
        		
        		System.out.println(i+"isis odd");
        	}
        	i++;
        }
	}

}
